import React, { Component } from 'react';
import { MDBBtn, MDBDataTable, MDBCollapse  } from 'mdbreact';
import Service from '../Service';


class ErrorLog extends Component {
    constructor (props) {
        super(props);
        this.state={
            totalCount: null,
            collapseID: "",
            columns: [
                {
                    label: "Error ID",
                    field: "error",
                },
                {
                    label: "Description",
                    field: "description",
                    width: 200,
                },
                {
                    label: "Flow",
                    field: "flow",
                    width: 100
                },
                {
                    label: "Error Type",
                    field: "type",
                    width: 100
                },
                {
                    label: "Emergency Level",
                    field: "emergency",
                    width: 100,
                },
                {
                    label: "Actions",
                    field: "actions",
                    width: 100,
                }
            ],
            rows: [],
        };
        this.refreshErrors = this.refreshErrors.bind(this);
        this.refreshTotalCount = this.refreshTotalCount.bind(this);
        this.rowAddActions = this.rowAddActions.bind(this);
    }

    componentDidMount() {
        this.refreshErrors();
        this.refreshTotalCount();
    }

    componentDidUpdate() {
        this.refreshErrors();
        this.refreshTotalCount();
    }

    refreshErrors() {
        // Service.retrieveAllErrors()
        //     .then(response => {
        //         this.setState({rows: response.data};
        //     })
        Service.retrieveAllErrors()
            .then(response => {
                this.rowAddActions(response.data);
            })
    }

    rowAddActions(data) {
        data = data.map((row) => {
            return (
                <li key = {row.error}>
                {row.error}
                {row.description}
                {row.flow}
                {row.type}
                {row.emergency}
                {actions = <button>button</button>}
                </li>
            )
        }
        );
        console.log(data);
        this.setState({rows: data});
    }

    refreshTotalCount() {
        Service.getErrorsTotalCount()
            .then(response => {
                this.setState({totalCount: response.data});
            })
    }

    // toggleCollapse = collapseID => () => {
