import React, { Component } from 'react';
import Select from 'react-select';
import 'bootstrap/dist/css/bootstrap.min.css';
import { text } from '@fortawesome/fontawesome-svg-core';


const flow = [
    { label: "All", value: 0},
  { label: "1", value: 1 },
  { label: "2", value: 2 },
  { label: "3", value: 3 },
  { label: "4", value: 4 },
  { label: "5", value: 5 },
  { label: "6", value: 6 }];

  const errorType = [
      { label: "All", value: 0},
    { label: "1", value: 1 },
    { label: "2", value: 2 },
    { label: "3", value: 3 },
    { label: "4", value: 4 },
    { label: "5", value: 5 },
    { label: "6", value: 6 }];

class LogFilter extends Component {
    state = {
        selectedOption: { label: "All", value: 0},
      }
      handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);
      }

    render() {
        const { selectedOption } = this.state;

        return (
            <div className="container" style={{
                padding: "30px",
                marginTop: "30px",
                justifyContent: "center",
                border: "1px solid #F7F7F7",
                height: "160px"
              }}>
                <h5 style={{
                    paddingLeft:"10px",
                    borderLeft: "5px solid #555555"
                }}>Error Log Filter</h5>


                <span style={{padding: "20px", display:"flex"}}> 
                    <h6 style={{ paddingTop: "15px"}}>Flow: </h6>
                    <Select className="mt-2 col-md-2 col-offset-4" options={flow} onChange={this.handleChange} autoFocus={true} />

                    <h6 style={{
                        paddingLeft: "60px",
                        paddingTop: "15px"
                    }}>Error Type: </h6>
                    <Select className="mt-2 col-md-2 col-offset-4" options={errorType} onChange={this.handleChange} autoFocus={true} />
                    <h6 style={{
                        paddingLeft: "60px",
                        paddingTop: "15px"
                    }}>Emergency Level: </h6>
                    <button type="button" class="btn btn-light" style={{margin: "5px"}}>All</button>
                    <button type="button" class="btn btn-danger" style={{margin: "5px"}}>Urgent</button>
                    <button type="button" class="btn btn-success" style={{margin: "5px"}}>Priority</button>
                    <button type="button" class="btn btn-secondary" style={{margin: "5px"}}>General</button>
                </span>



            </div>

        )
    }
}

export default LogFilter;
