        details: "details 6",
        state: "live"
    },
    {
        id: 7,
        name: "name 7",
        details: "details 7",
        state: "live"
    },
    {
        id: 8,
        name: "name 8",
        details: "details 8",
        state: "live"
    },
    {
        id: 9,
        name: "name 9",
        details: "details 9",
        state: "live"
    },
    {
        id: 10,
        name: "name 10",
        details: "details 10",
        state: "live"
    }                
];

let cols = [
    {
        icon: "",
        label: "Order Number"
    },
    {
        icon: "",
        label: "Name"
    },
    {
        icon: "",
        label: "Details"
    },
    {
        icon: "",
        label: "State"
    }  
]

class Table extends Component {
    constructor() {
      super();

      this.state = {
        headerOffset: null,
        headerFixed: true
      }
    }

  //   handleScroll(e) {

  //     let scrollTop = e.srcElement.body.scrollTop;
  //     console.log('scroll...', scrollTop, this.state.headerOffset);




  //     this.setState({
  //       headerFixed: true
  //     });
  //   }

    componentDidMount() {
    //   window.addEventListener('scroll', this.handleScroll.bind(this));
      // THIS SEEMS THE ONLY PLACE WE CAN PICK UP THE REF FOR THE HEADER
      console.log('reactdom: ', ReactDOM.findDOMNode(this.refs.header));

      store.headerOffset = ReactDOM.findDOMNode(this.refs.header).getBoundingClientRect().top;  


      console.log('store:', store.headerOffset);


      // this.setState({headerOffset:ReactDOM.findDOMNode(this.refs.header)});
    }

    render(){

      let columns = this.props.columns.map((item, inx) => {
        return (<HeaderColumn label={item.label}/>);
      });    

      //go through the rows
      let rows = this.props.data.map((item, inx) => {
        return (<RowItem {...item}></RowItem>);
      });

      let classes = 'header'; 
      if (this.props.headerFixed) {
        classes = 'header fixed';
      }

     return (
        <div className="table">
            {this.props.children} 
            <div className={classes} ref="header">
            {columns}
                <div className="shadow"></div>
            </div>
            <ul>{rows}</ul>
        </div>
    ); 
    }
  }

  export default Table;
