import React, { Component } from 'react';
import Select from 'react-select';
import 'bootstrap/dist/css/bootstrap.min.css';
import { text } from '@fortawesome/fontawesome-svg-core';


const flow = [
  { label: "1", value: 1 },
  { label: "2", value: 2 },
  { label: "3", value: 3 },
  { label: "4", value: 4 },
  { label: "5", value: 5 },
  { label: "6", value: 6 }];

  const errorType = [
    { label: "1", value: 1 },
    { label: "2", value: 2 },
    { label: "3", value: 3 },
    { label: "4", value: 4 },
    { label: "5", value: 5 },
    { label: "6", value: 6 }];

class LogFilter extends Component {
    state = {
        selectedOption: null,
      }
      handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);
      }

    render() {
        const { selectedOption } = this.state;

        return (
            <div className="container" style={{
                paddingTop: "60px"
              }}>
                <h5 style={{
                    paddingLeft:"10px",
                    borderLeft: "5px solid #555555"
                }}>Error Log Filter</h5>


                <span style={{padding: "20px", display:"flex"}}> 
                    <h6>Flow: </h6>
                    <Select className="mt-2 col-md-2 col-offset-4" options={flow} onChange={this.handleChange} autoFocus={true}/>
                </span>

                <span style={{padding: "20px", display:"flex"}}> 
                    <h6>Error Type: </h6>
                    <Select className="mt-2 col-md-2 col-offset-4" options={errorType} onChange={this.handleChange} autoFocus={true} />
                </span>
                <span style={{padding: "20px", display:"inline-block"}}> 
                    hello
                </span>



            </div>

        )
    }
}

export default LogFilter;
