import React, { Component } from 'react';
import { MDBBtn, MDBDataTable, MDBCollapse  } from 'mdbreact';
import ErrorsService from '../ErrorsService';


class ErrorLog extends Component {
    constructor (props) {
        super(props);
        this.state={
            totalCount: 0,
            collapseID: "",
            columns: [
                {
                    label: "Error ID",
                    field: "error",
                },
                {
                    label: "Description",
                    field: "description",
                    width: 400,
                },
                {
                    label: "Flow",
                    field: "flow",
                    width: 80
                },
                {
                    label: "Error Type",
                    field: "type",
                    width: 80
                },
                {
                    label: "Emergency Level",
                    field: "emergency",
                    width: 80,
                }
            ],
            rows: [],
        };
        this.refreshErrors = this.refreshErrors.bind(this);
    }

    componentDidMount() {
        this.refreshErrors;
    }

    refreshErrors() {
        ErrorsService.retrieveAllErrors()
            .then(response => {
                console.log(response);
                this.setState({rows: response.data});
            })
    }

    toggleCollapse = collapseID => () => {
        this.setState(prevState => ({
          collapseID: prevState.collapseID !== collapseID ? collapseID : ""
        }));
    }


